sp_widget.script:
(function () {
/* populate the 'data' object */
/* e.g., data.table = $sp.getValue('table'); */

data.arts = [];

class KbUtils {
TABLE_NAME = ""
constructor() {
  this.limit = 50,
  this.TABLE_NAME = "kb_knowledge"
}

getKbArticles = () => {
  gs.addInfoMessage(this.TABLE_NAME)
  let kbGr = new GlideRecord(this.TABLE_NAME);
  kbGr.setLimit(this.limit);
  kbGr.addEncodedQuery("workflow_state=published");
  kbGr.query();

  while (kbGr.next()) {
    let doc = {};
    doc.sd = kbGr.getValue("short_description");
    doc.author = kbGr.getValue("author");

    data.arts.push(doc);
  }

}

}

class KbUtils2 extends KbUtils{
  info = () =>{
    gs.addInfoMessage(`From child: `,+TABLE_NAME);
  }
}

new KbUtils2().getKbArticles();
new KbUtils2().info();
//gs.addInfoMessage(JSON.stringify(data.arts))
})();
