sp_widget.client_script:
api.controller = function (spUtil, $http) {
	/* widget controller */
	var c = this;
	c.html = "";
	c.query = "";
	c.timeTaken = 0;
	c.startTime = "";
	c.score = 0;
	var ans = document.getElementById("answer");


	var converter = new showdown.Converter();
	//c.html = converter.makeHtml(md);

	c.clear = () =>{
		ans.innerHTML = "";
		c.score = 0;
		c.timeTaken = 0;
	};

	c.llmSearch = (query) => {

		// Check for answer in L1 cache.
		c.startTime = new Date();
		c.server.get({ action: "ansInL1", query: query }).then(function (answer) {

			if (answer.data.ans == "" || answer.data.ans == null) {

				// RAG via Ai Search
				var config = [{
					definitionSysId: '0cac8b3073ad101052c7d5fdbdf6a78a',
					type: 'GRAPHQL',
					inputValues: {
						searchContextConfigId: {
							type: 'JSON_LITERAL',
							value: "7296910f53171010069addeeff7b12e7"
						},
						searchTerm: {
							type: 'JSON_LITERAL',
							value: query
						},
						searchEvamConfigId: {
							type: 'JSON_LITERAL',
							value: "9264a1c353571010069addeeff7b121e"
						},
						facetFilters: {
							type: 'JSON_LITERAL',
							value: null
						},
						searchFilters: {
							type: 'JSON_LITERAL',
							value: []
						},
						disableSpellCheck: {
							type: 'JSON_LITERAL',
							value: 'false'
						},
						paginationToken: {
							type: 'JSON_LITERAL',
							value: null
						},
						sortOptions: {
							type: 'JSON_LITERAL',
							value: []
						},
						asyncParams: {
							type: 'JSON_LITERAL',
							value: '{asyncMode: \"GR_ONLY\", callbackType: \"AMB\"}'
						}
					}
				}];

				var url = spUtil.getHost() + '/api/now/uxf/databroker/exec';

				$http.post(url, config).then(function (response) {

					// Process AIS response
					c.items = response.data.result[0].executionResult.searchResultsTemplates.items;
					c.kbList = c.items.map(item => (item.propValues.model.table == "kb_knowledge" ? item.propValues.model.number : ""));
					c.kbList = c.kbList.filter(item => item != "");

					// Generate Context from KB article body
					c.server.get({ action: "getContext", kbNums: c.kbList }).then(function (resp) {


						c.context = resp.data.context;

						// call Now LLM
						c.server.get({ action: "llmSearch", query: query, context: c.context }).then(function (res) {

							// Parse Now LLM answer
							var ans = document.getElementById("answer");
							c.html = converter.makeHtml(res.data.ans);
							ans.innerHTML = c.html;
							c.timeTaken = (new Date() - c.startTime) / 1000 + "s";


						});

					});



				});

			}
			else {
				c.score = answer.data.score;
				c.html = converter.makeHtml(answer.data.ans);
				ans.innerHTML = c.html;
				c.timeTaken = (new Date() - c.startTime) / 1000 + "s";

			}

		});



	}
};

sp_widget.script:
(function () {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */


	data.ans = "";
	data.context = "";
	data.similarQuery = "";
	data.score = 0;
	var searchEvent = new global.BuildSuggestionsFromLogTable();

	if (input && input.action == "ansInL1") {

		var searchGr = new GlideRecord("sys_search_event");
		searchGr.addEncodedQuery("has_results=true");
		searchGr.query();

		while (searchGr.next()) {

			var inputsPayload = {};
			// query1 is a input of type: string
			inputsPayload['query1'] = input.query;
			// query2 is a input of type: string
			inputsPayload['query2'] = searchGr.getValue("search_query");

			var request = {
				executionRequests: [{
					payload: inputsPayload,
					capabilityId: 'e88e304cf52212107f44806fa872f83a',
					meta: {
						skillConfigId: 'fc8e7040f56212107f44806fa872f8fa'
					}
				}],
				mode: 'sync'
			};

			try {
				var resp = (sn_one_extend.OneExtendUtil.execute(request));
				var score = JSON.parse(resp["capabilities"]["e88e304cf52212107f44806fa872f83a"]["response"]).model_output.split("\n")[0];
				data.score = score;
				if (score >= 60) {
					data.similarQuery = searchGr.getValue("search_query");

					data.ans = sn_scoped_cache.ScopedCacheManager.get("svngenaicache", data.similarQuery);
					if (data.ans) {
						searchEvent._insertSearchEvents("x_1234_genai", "GenAi", input.query, gs.getUserID(), gs.getSessionID(), true, gs.getSession().getLanguage());
					}
					break;
				}
			} catch (e) {
				gs.error(e);
				gs.addErrorMessage('Something went wrong while executing skill Text Similarity' + e.getMessage());
			}

		}
	}


	if (input && input.action == "getContext") {
		input.kbNums.forEach(function (kbNum) {
			var kbGr = new GlideRecord("kb_knowledge");
			kbGr.addQuery("latest=true^workflow_state=published^sys_class_name=kb_knowledge^number=" + kbNum);
			kbGr.setLimit(2);
			kbGr.query();

			while (kbGr.next()) {
				data.context += kbGr.getValue("text");
			}

		});
	}



	if (input && input.action === "llmSearch") {

		try {
			var inputs = {};
			inputs['query'] = input.query; // String 
			inputs['context'] = input.context;
			// Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
			// sn_fd.FlowAPI.getRunner().action('x_1234_genai.llm_search').inBackground().withInputs(inputs).run();

			// Execute Synchronously: Run in foreground. Code snippet has access to outputs.
			var result = sn_fd.FlowAPI.getRunner().action('x_1234_genai.llm_search').inForeground().withInputs(inputs).run();
			var outputs = result.getOutputs();

			// Get Outputs:
			// Note: outputs can only be retrieved when executing synchronously.
			data.ans = outputs['answer']; // String
			searchEvent._insertSearchEvents("x_1234_genai", "GenAi", input.query, gs.getUserID(), gs.getSessionID(), true, gs.getSession().getLanguage());
			// Push the answer to L1 cache
			sn_scoped_cache.ScopedCacheManager.put("svngenaicache", input.query, data.ans);

		} catch (ex) {
			var message = ex.getMessage();
			gs.addErrorMessage('Something went wrong while executing skill - generate answer' + ex.getMessage());
		}


	}
})();
