sys_variable_value.value:
(function execute(inputs, outputs) {
    var inputsPayload = {};
    // query is a input of type: string
    inputsPayload['query'] = inputs.query;
    // context is a input of type: string
    inputsPayload['context'] = inputs.context;

    var request = {
        executionRequests: [{
            payload: inputsPayload,
            capabilityId: '82c5957c831212107f443135afda1e8c',
            meta: {
                skillConfigId: '9ec5957c831212107f443135afda1ed2'
            }
        }],
        mode: 'sync'
    };

    try {
        var ans = sn_one_extend.OneExtendUtil.execute(request);
       outputs.answer = JSON.parse(ans["capabilities"]["82c5957c831212107f443135afda1e8c"]["response"])["model_output"];
    } catch (e) {
        gs.error(e);
        gs.addErrorMessage('Something went wrong while executing skill.');
    }

})(inputs, outputs);

