sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const key = event.payload.column.key;
    const getSkillsInput = api.state.getSkillsInput;
    if(!getSkillsInput.order_condition)
        getSkillsInput.order_condition = {};

    if (api.state.columnOrder) {
        getSkillsInput.order_condition.direction = 'descending';
        api.setState('columnOrder', false);
        api.setState('orderedHeader', event.payload.column.label);
    } else {
        api.setState('columnOrder', true);
        getSkillsInput.order_condition.direction = 'ascending';
        api.setState('orderedHeader', event.payload.column.label);
    }
    getSkillsInput.order_condition.attribute = key;
    api.setState('getSkillsInput', getSkillsInput);
}
