sys_ux_client_script.required_translations:
[
    {
        "message": "This is a required field"
    },
    {
        "message": "This skill name already exists. Enter a unique skill name."
    },
    {
        "message": "Enter a valid Name."
    }
]

sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    const nameValidation = api.state.validations.nameValidation;
    const nameRegex = new RegExp(nameValidation);
    if (api.state.skillName === "") {
        return [{
            status: 'critical',
            content: helpers.translateSync("This is a required field"),
            icon: "exclamation-triangle-outline",
        }];
    }
    if (api.state.skillNameExists) {
        return [{
            status: 'critical',
            content: helpers.translateSync("This skill name already exists. Enter a unique skill name."),
            icon: "exclamation-triangle-outline",
        }];

    } else if (!nameRegex.test(api.state.skillName)) {
        return [{
            status: 'critical',
            content: helpers.translateSync("Enter a valid Name."),
            icon: "exclamation-triangle-outline",
        }];

    }
    return [];
}
