sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
     if(event.elementId === "create_skill" && event.name === "DATA_OP_SUCCEEDED"){
        if(event.payload.data.output.capabilityId === "" && event.payload.data.output.skillConfigId === "" && event.payload.data.output.skillNameExists){
            api.setState('skillNameExists', event.payload.data.output.skillNameExists);

        }
        if(event.payload.data.output.capabilityId && event.payload.data.output.skillConfigId && !event.payload.data.output.skillNameExists){
            api.setState('capabilityId', event.payload.data.output.capabilityId);
            helpers.navigate.to('skill', {skillId: event.payload.data.output.capabilityId}, { promptId: 'prompt1', configId: event.payload.data.output.skillConfigId, newSkill: true});
            helpers.modal.close('create_prompt_modal');
            return;
        }
        if(event.payload.data.output.capabilityId && !event.payload.data.output.skillNameExists){
            helpers.navigate.to('skill', {skillId: event.payload.data.output.capabilityId},  {promptId: 'prompt1', newSkill: true});
            helpers.modal.close('create_platform_skill_modal');
        }
    }
}
