sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
	var columnsDatatypes = api.state.columnsDatatypes;
    var id = api.state.selecteFilterField;
    if (columnsDatatypes && Array.isArray(columnsDatatypes) && columnsDatatypes.length > 0) {
        const column = columnsDatatypes.find(column => column.id === id);
        const dataTypes = column && column.dataType;
        if (dataTypes && Array.isArray(dataTypes) && dataTypes.length > 0) {
            return dataTypes.map(datatype => ({
                id: datatype.id,
                label: datatype.label,
            }));
        }
    }
    return [];
}
