sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    if (event.elementId === "fetch_platform_skills" && event.name === "DATA_OP_SUCCEEDED") {
         if(!event.payload.data.output.datapresent){
              api.setState('loadPlatformSKills', true);
               api.setState('platformSkillsError', false);
            return;
        } else {
        if (event.payload.data.output.rows) {
            api.setState('platformRows', {
                'rows': event.payload.data.output.rows
            });
            const getPlatformSkillsInput = api.state.getPlatformSkillsInput;
            const pageNumber = ((getPlatformSkillsInput || {}).page_condition || {}).offset;
            const recordsPerPage = ((getPlatformSkillsInput || {}).page_condition || {}).records_per_page;
            const startIndex = pageNumber * recordsPerPage;
            const endIndex = startIndex + recordsPerPage;

             const columnKey = "Last modified";
            const order = "order";


           var filteredRows = {
        rows: (event.payload.data.output.rows || []).sort((a, b) => {
            const aValue = a.cells[columnKey] && a.cells[columnKey].value ? a.cells[columnKey].value.toLowerCase() : '';
            const bValue = b.cells[columnKey] && b.cells[columnKey].value ? b.cells[columnKey].value.toLowerCase() : '';

            if (aValue < bValue) {
                return order === 'ascending' ? -1 : 1;
            }
            if (aValue > bValue) {
                return order === 'ascending' ? 1 : -1;
            }
            return 0;
        })
    };
      const finalRows = filteredRows.rows.slice(startIndex, endIndex);
            api.setState('platformFilteredRows', {
                'rows': finalRows
            });
            api.setState('platformTotalRows', event.payload.data.output.rows.length);

        }
        if (event.payload.data.output.columns) {
            var column = event.payload.data.output.columns.columns.find(col => col.label === "Last Updated");
            if (column) {
                column.icons.endIcons[0].value = 'caret-down-fill';
            }

            api.setState('platformColumns', event.payload.data.output.columns);
        }
    }
    }
}
